mapper.select:
select
d.id,d.user_id,d.organization_id,d.department_id,d.medical_institution,d.medical_category,
d.status,d.created_by,d.created_time,d.updated_by,d.updated_time
from researcher_info d inner join user_info u on (d.user_id = u.id)


        and d.user_id = #{userId, jdbcType=BIGINT}


        and u.real_name like CONCAT('%', #{realName,jdbcType=VARCHAR},'%')


        and u.phone like CONCAT('%', #{phoneLike,jdbcType=VARCHAR},'%')


        and u.status = #{userStatus,typeHandler=com.soyoung.base.mybatis.handler.MyBaseTypeHandler}


        and d.status = #{status,typeHandler=com.soyoung.base.mybatis.handler.MyBaseTypeHandler}


        and d.organization_id = #{organizationId, jdbcType=BIGINT}


        and d.department_id = #{departmentId, jdbcType=BIGINT}
